I've written a small PHP script to check the total amount of documents given by ES in the initial scroll response vs the document count to exactly see how much documents are not retrieved. See below.

```php
<?php
$query = '{
	"size": 1000,
   	"filter": {
     	"bool": {
       		"must": []
     	}
   	},   
}';

$search = new ScrollSearch($query);

class ScrollSearch
{
	private $scrollId = null;
	private $totalDocuments = null;
	private $totalResultDocuments = 0;

	private $shards = array(
		'total' => 0,
		'successful' => 0,
		'failed' => 0
	);

	public function __construct($query) 
	{
		$this->initScrollScan($query);

		while ($this->scrollId != false) {
			$this->getDocuments();
		}

		$this->printInfo();
		echo "COMPLETED" . PHP_EOL;
	}

	private function initScrollScan()
	{
		$ch = curl_init("http://127.0.0.1:9200/20141016v3/accesslog/_search?scroll=10m&search_type=scan");
		curl_setopt( $ch, CURLOPT_POSTFIELDS, $data);
		curl_setopt( $ch, CURLOPT_HTTPHEADER, array('Content-Type:application/json'));
		curl_setopt( $ch, CURLOPT_RETURNTRANSFER, true );
		$response = curl_exec($ch);
		$response = json_decode($response);

		$this->scrollId = $response->{'_scroll_id'};
		$this->totalDocuments = $response->{'hits'}->{'total'};
		
		curl_close($ch);
	}

	private function getDocuments()
	{
		$ch = curl_init("http://127.0.0.1:9200/_search/scroll?scroll=10m");
		curl_setopt( $ch, CURLOPT_POSTFIELDS, $this->scrollId);
		curl_setopt( $ch, CURLOPT_HTTPHEADER, array('Content-Type:application/json'));
		curl_setopt( $ch, CURLOPT_RETURNTRANSFER, true );
		$json = curl_exec($ch);
		$response = json_decode(utf8_encode($json));
		$countDocuments = count($response->{'hits'}->{'hits'});
		$info = curl_getinfo($ch);

		$this->shards['total'] += $response->{'_shards'}->{'total'};
		$this->shards['successful'] += $response->{'_shards'}->{'successful'};
		$this->shards['failed'] += $response->{'_shards'}->{'failed'};

		if ($info['http_code'] != 200) {
			echo "Request goes wrong: " . $info['http_code'] . '. Retry.' .PHP_EOL;
			getDocuments();
		}

		if ($countDocuments > 0) {
			$this->scrollId = $response->{'_scroll_id'};
			$this->totalResultDocuments += $countDocuments;
			$this->printInfo();
		} else {
			echo 'NO MORE RESULTS!' . PHP_EOL;
			$this->scrollId = false;
		}

		curl_close($ch);
	}

	private function printInfo() {
		echo ($this->totalResultDocuments - $this->totalDocuments) . " missing documents. " . $this->shards['total'] . " shards, " . $this->shards['failed'] . " failed and " . $this->shards['successful'] . " successful" . PHP_EOL;
	}

}
